sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '' || g_form.getValue('duration' == 'true') || g_form.getValue('leave_start_date') == '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    var startDateString = g_form.getValue('leave_start_date');
    if (startDateString != '' || startDateString != null) {
        var startDate = new Date(startDateString);
        var endDate = new Date(newValue);
        var Difference_In_Time = endDate.getTime() - startDate.getTime();
        var Difference_In_Days = Math.round(Difference_In_Time / (1000 * 3600 * 24));
        Difference_In_Days += 1;
        if (Difference_In_Days <= 0) {
            g_form.setValue('leave_end_date', startDateString);
            Difference_In_Days = 1;
        }
        g_form.setValue('duration', Difference_In_Days);
    }
}
